import Foundationimport MapKitimport Contactsimport AddressBookclass Location: NSObject, MKAnnotation {    let title: String?    let locationName: String    let discipline: String    let coordinate: CLLocationCoordinate2D        init(title: String, locationName: String, discipline: String, coordinate: CLLocationCoordinate2D) {        self.title = title        self.locationName = locationName        self.discipline = discipline        self.coordinate = coordinate                super.init()    }        var subtitle: String? {        return locationName    }            func mapView(mapView: MKMapView!, annotationView view: MKAnnotationView!,                 calloutAccessoryControlTapped control: UIControl!) {        let location = view.annotation as! Location        let launchOptions = [MKLaunchOptionsDirectionsModeKey: MKLaunchOptionsDirectionsModeDriving]        location.mapItem().openInMaps(launchOptions: launchOptions)    }        func pinTintColor() -> UIColor  {        switch discipline {            case "Blocked Roads":        return MKPinAnnotationView.purplePinColor()            case "Infrastructure":        return MKPinAnnotationView.purplePinColor()            case "Sanitation":        return MKPinAnnotationView.greenPinColor()            default:        return MKPinAnnotationView.redPinColor()        }    }        func mapItem() -> MKMapItem {        let addressDict = [CNPostalAddressStreetKey: subtitle!]        let placemark = MKPlacemark(coordinate: coordinate, addressDictionary: addressDict)            let mapItem = MKMapItem(placemark: placemark)        mapItem.name = title            return mapItem    }    }